import express from 'express'
const server = express()

import dotenv from 'dotenv'
dotenv.config()

import apiConfig from '../routes' // folder van hieu, vo index is, roi vo tipe v1
import  viewConfig  from '../views'


// import userRouter from '../routes/api/users'
//const userRouter = require('../routes/users')
import bodyParser from 'body-parser'

// parse application/x-www-form-urlencoded
server.use(bodyParser.urlencoded({ extended: false }))
// parse application/json
server.use(bodyParser.json())




//setUp Cors để control dc lỗi giới hạn quyền truy cập
import cors from 'cors'
server.use(cors())

//setupView 
server.use('/view', viewConfig)


//setUp API
server.use('/apis', apiConfig)

server.listen(process.env.SERVER_PORT, ()=>{
    console.log(`server running on port ${process.env.SERVER_HOST}: ${process.env.SERVER_PORT}`);
})

