    res.json(userData); // muon gui ve duoi dang json, dung res.send cung dc
    // console.log('typeof: ', typeof userData) // log cai nay ra se la kieu du lieu object 
});


// Lấy theo id
router.get('/:userId', (req, res) => {
    const { userId } = req.params;
    const user = userData.find(item => item.id == userId);
    if (user) {
        res.json(user);
    } else {
        res.status(404).json({ message: 'User nont found' });
    }
})

router.post('/', (req, res) => { // nay /users de biet tao tren truong nao khi co nhieu cai json
    // muốn update thì phải có payload
    const { name, email } = req.body
    console.log('req.body', req.body)
    const newUser = { id: (userData.length + 1) + 1, name, email }
    userData.push(newUser)
    fs.writeFileSync(userDataPath, JSON.stringify(userData))
    res.json(newUser)
})

router.put('/:userId', (req, res) => {
    const { userId } = req.params
    const { username, email } = req.body
    const userIndex = userData.findIndex(user => user.id == userId)
