import  express  from 'express'
import fs from 'fs'
//const fs = require('fs')
//const app = express() // sử dụng thư viện express , dung router thi ko can dung bien app nua
const router=express.Router()
//import bodyParser from 'body-parser'
//const bodyParser = require('body-parser')

import { checkAdmin } from '../../middlewares/user.middleware' // hoặc import cái tên file ko ngoặc rồi ở bên dưới
// lấy cái tên file và chấm checkAdmin

const userDataPath = './dev-data/users.json'

//const port = 8000
let userData = JSON.parse(fs.readFileSync(userDataPath, 'utf-8'))

router.get('/',checkAdmin, (req, res) => {
    res.json(userData); // muon gui ve duoi dang json, dung res.send cung dc
    // console.log('typeof: ', typeof userData) // log cai nay ra se la kieu du lieu object 
});

// Lấy theo id
router.get('/:userId', (req, res) => {
    const { userId } = req.params;
    const user = userData.find(item => item.id == userId);
    if (user) {
        res.json(user);
    } else {
        res.status(404).json({ message: 'User nont found' });
    }
})

router.post('/', (req, res) => { // nay /users de biet tao tren truong nao khi co nhieu cai json
    // muốn update thì phải có payload
    const { name, email } = req.body
    console.log('req.body', req.body)
    const newUser = { id: (userData.length + 1) + 1, name, email }
    userData.push(newUser)
    fs.writeFileSync(userDataPath, JSON.stringify(userData))
    res.json(newUser)
})

router.put('/:userId', (req, res) => {
    const { userId } = req.params
    const { username, email } = req.body
    const userIndex = userData.findIndex(user => user.id == userId)
    if (userIndex !== -1) {
        userData[userIndex].username = username
        userData[userIndex].email = email
        fs.writeFileSync(userDataPath, JSON.stringify(userData))
        res.json(userData[userIndex])
    } else {
        res.status(404).json({ message: 'User not found' })
    }
})

router.delete('/:userId', (req, res) => {
    const { userId } = req.params;
    const userIndex = userData.findIndex(user => user.id == userId); // xai filer cung dc
    if (userIndex !== -1) {
        userData.splice(userIndex, 1);
        fs.writeFileSync(userDataPath, JSON.stringify(userData));
        res.json({ message: 'User deleted successfull' });
    } else {
        res.status(404).json({ message: 'User not found' });
    }
}); 

router.use(express.static('public'))
//router.get('/', (req, res) => { // dùng phương thức get: lấy, server gửi cho client 1 cái giá trị hello world
//  res.send('Hello World!')
//})

//router.get('/users/:userId', (req, res)=>{ // tự truyền ID vào trên localhost
//  res.send(req.params) // lấy dc tham số phía trên truyền vào
//  console.log(req.params);
//})

//router.get('/example/b', (req, res, next) => {
// console.log('the response will be sent by the next function ...') // ra cái này trc rồi mới ra hello from B
// next()
//}, (req, res) => {
//   res.send('Hello from B!')
//})



module.exports = router;

