import  express  from 'express'
import fs from 'fs'
const router=express.Router()

import { checkAdmin } from '../../middlewares/user.middleware' 

const userDataPath = './dev-data/users.json'

let userData = JSON.parse(fs.readFileSync(userDataPath, 'utf-8'))

router.get('/',checkAdmin, (req, res) => {
    res.json(userData); 

});

// Lấy theo id
router.get('/:userId', (req, res) => {
    const { userId } = req.params;
    const user = userData.find(item => item.id == userId);
    if (user) {
        res.json(user);
    } else {
        res.status(404).json({ message: 'User nont found' });
    }
})

router.post('/', (req, res) => {

    const { name, email } = req.body
    console.log('req.body', req.body)
    const newUser = { id: (userData.length + 1) + 1, name, email }
    userData.push(newUser)
    fs.writeFileSync(userDataPath, JSON.stringify(userData))
    res.json(newUser)
})

router.put('/:userId', (req, res) => {
    const { userId } = req.params
    const { username, email } = req.body
    const userIndex = userData.findIndex(user => user.id == userId)
    if (userIndex !== -1) {
        userData[userIndex].username = username
        userData[userIndex].email = email
        fs.writeFileSync(userDataPath, JSON.stringify(userData))
        res.json(userData[userIndex])
    } else {
        res.status(404).json({ message: 'User not found' })
    }
})

router.delete('/:userId', (req, res) => {
    const { userId } = req.params;
    const userIndex = userData.findIndex(user => user.id == userId); 
    if (userIndex !== -1) {
        userData.splice(userIndex, 1);
        fs.writeFileSync(userDataPath, JSON.stringify(userData));
        res.json({ message: 'User deleted successfull' });
    } else {
        res.status(404).json({ message: 'User not found' });
    }
}); 

router.use(express.static('public'))


module.exports = router;

