import express from 'express';
import fs from 'fs';

const router = express.Router();
const dataFilePath = './dev-data/questions.json'

// Get all people
router.get('/', (req, res) => {
    fs.readFile(dataFilePath, 'utf8', (err, data) => {
        if (err) {
            return res.status(500).json({ message: 'Error reading data' });
        }
        const people = JSON.parse(data);
        res.json(people);
    });
});

// Get a person by ID
router.get('/:id', (req, res) => {
    const id = parseInt(req.params.id);
    fs.readFile(dataFilePath, 'utf8', (err, data) => {
        if (err) {
            return res.status(500).json({ message: 'Error reading data' });
        }
        const people = JSON.parse(data);
        const person = people.find((p) => p.id === id);
        if (!person) {
            return res.status(404).json({ message: 'Person not found' });
        }
        res.json(person);
    });
});

// Create a new person
router.post('/', (req, res) => {
    const { name } = req.body;
    if (!name) {
        return res.status(400).json({ message: 'Name is required' });
    }

    fs.readFile(dataFilePath, 'utf8', (err, data) => {
        if (err) {
            return res.status(500).json({ message: 'Error reading data' });
        }
        const people = JSON.parse(data);
        const newPerson = { id: people.length + 1, name };
        people.push(newPerson);
        const newData = JSON.stringify(people);

        fs.writeFile(dataFilePath, newData, 'utf8', (err) => {
            if (err) {
                return res.status(500).json({ message: 'Error writing data' });
            }
            res.status(201).json(newPerson);
        });
    });
});

// Update a person by ID
router.put('/:id', (req, res) => {
    const id = parseInt(req.params.id);
    const { name } = req.body;
    fs.readFile(dataFilePath, 'utf8', (err, data) => {
        if (err) {
            return res.status(500).json({ message: 'Error reading data' }); 
        }
        const people = JSON.parse(data);
        const personIndex = people.findIndex((p) => p.id === id);
        if (personIndex === -1) {
            return res.status(404).json({ message: 'Person not found' });
        }
        people[personIndex].name = name;
        const newData = JSON.stringify(people);

        fs.writeFile(dataFilePath, newData, 'utf8', (err) => {
            if (err) {
                return res.status(500).json({ message: 'Error writing data' });
            }
            res.json(people[personIndex]);
        });
    });
});

// Delete a person by ID
router.delete('/:id', (req, res) => {
    const id = parseInt(req.params.id);
    fs.readFile(dataFilePath, 'utf8', (err, data) => {
        if (err) {
            return res.status(500).json({ message: 'Error reading data' });
        }
        const people = JSON.parse(data);
        const personIndex = people.findIndex((p) => p.id === id);
        if (personIndex === -1) {
            return res.status(404).json({ message: 'Person not found' });
        }
        const deletedPerson = people.splice(personIndex, 1)[0];
        const newData = JSON.stringify(people);

        fs.writeFile(dataFilePath, newData, 'utf8', (err) => {
            if (err) {
                return res.status(500).json({ message: 'Error writing data' });
            }
            res.json(deletedPerson);
        });
    });
});

